var easyFloater={init:function(a){var b=this.iPad_or_iPhone();var c=this.createElement(a);if(!b){this.embedFlash(a)}else{this.alternativeContent(a)}},createElement:function(a){if(document.createElement){document.body.prependChild=function(c){this.insertBefore(c,this.firstChild)};var b=document.createElement("div");b.setAttribute("id","easyFloater");b.style.backgroundColor=a.color;b.style.position="absolute";b.style.zIndex="9999";b.style.width=a.width+"px";b.style.height=a.height+"px";b.style.top=a.top+"px";b.style.left="50%";b.style.marginLeft=(-(a.width/2))+"px";document.body.prependChild(b);return true}},embedFlash:function(a){flashembed("easyFloater",{src:a.src,w3c:true,wmode:"transparent"},a.flashVars)},alternativeContent:function(a){var b=document.getElementById("easyFloater");b.style.backgroundColor="transparent";var c=document.createElement("div");c.setAttribute("id","right_deprecated");c.style.textAlign="right";c.innerHTML='<a onClick="removeFloater()" id="closeFloater"><img style="float:right;" src="http://github.com/brunomarks/Easy-Floater-JS/raw/master/demo/imagens/close.png" alt="Fechar" /></a><br/><img src="'+a.alternativeBg+'" />';b.appendChild(c)},iPad_or_iPhone:function(){if((navigator.userAgent.match(/iPhone/i))||(navigator.userAgent.match(/iPod/i))){return true}else{return false}}};function removeFloater(){var c=document.getElementById("easyFloater");var a=c.parentNode;var b=a.removeChild(c)};
